package com.tulin.common.utlis;

import org.apache.commons.codec.binary.Base64;
import org.springframework.web.multipart.MultipartFile;

import java.io.*;

/**
 * @author tpp
 */
public class Base64ToMultipartFile implements MultipartFile {
	private final byte[] imgContent;
	private final String header;

	public Base64ToMultipartFile(byte[] imgContent, String header) {
		this.imgContent = imgContent;
		this.header = header.split(";")[0];
	}

	@Override
	public String getName() {
		return System.currentTimeMillis() + Math.random() + "." + header.split("/")[1];
	}

	@Override
	public String getOriginalFilename() {
		return System.currentTimeMillis() + (int) Math.random() * 10000 + "." + header.split("/")[1];
	}

	@Override
	public String getContentType() {
		return header.split(":")[1];
	}

	@Override
	public boolean isEmpty() {
		return imgContent == null || imgContent.length == 0;
	}

	@Override
	public long getSize() {
		return imgContent.length;
	}

	@Override
	public byte[] getBytes() throws IOException {
		return imgContent;
	}

	@Override
	public InputStream getInputStream() throws IOException {
		return new ByteArrayInputStream(imgContent);
	}

	@Override
	public void transferTo(File file) throws IOException {
		FileOutputStream fileOutputStream = new FileOutputStream(file);
		fileOutputStream.write(imgContent);
		fileOutputStream.close();
	}

	public static MultipartFile base64ToMultipart(String base64) {
		String[] baseStrs = base64.split(",");
		Base64 decoder = new Base64();
		byte[] b = new byte[0];
		b = decoder.decode(baseStrs[1]);
		for (int i = 0; i < b.length; ++i) {
			if (b[i] < 0) {
				b[i] += 256;
			}
		}
		return new Base64ToMultipartFile(b, baseStrs[0]);
	}
	
	public static void main(String[] args) throws IllegalStateException, IOException {
		MultipartFile file = base64ToMultipart("data:image/png;base64,");
		// 文件名
		String originalFileName = file.getOriginalFilename();
		// 文件后缀
		String suffix = originalFileName.substring(originalFileName.lastIndexOf("."));
		// 生成新的文件名,规则: UUID + YMDHMS日期格式 + 文件后缀
		String newFileName = file.getOriginalFilename() + "-" + suffix;
		File newFile = new File("C:\\" + newFileName);
		if (!newFile.getParentFile().exists()) {
			newFile.getParentFile().mkdirs();
		}
		// 将内存中的数据写入磁盘
		file.transferTo(newFile);
	}
}